import React, { useState } from 'react';
import { Button, Typography, TextField } from '@mui/material';
import { Link } from 'react-router-dom';
import { styled } from '@mui/material/styles';

const Input = styled('input')({
  display: 'none',
});

const Decrypt = () => {
  const [arr , setArr] = useState("");
  const [name , setName] = useState();

  const upload =(e)=>{
    var str=e.target.value
    var arr=str.split('fakepath');
       setArr(arr)
  }

  return (
    <div>
      <div className='Header'>
      <Link className='link' to="/">
        <Typography variant="h3" >MINI-PROJECT</Typography>
      </Link>
        <div></div>
      </div>
      {1 && vari ? <div className='container'>
      <h1>DECRYPTION</h1>
        <div className='typewriter'>
          <h3>Upload encpt image along with key..</h3>
        </div>
        <label htmlFor="contained-button-file">
        <Input accept="image/*" id="contained-button-file" multiple type="file" onChange={upload} />
        <Button variant="contained" component="span">
          Choose file
        </Button>
        <br></br><br></br>
        {arr[1]}
        <br></br><br></br>
        <TextField
          id="outlined-number"
          label="Number"
          type="number"
          InputLabelProps={{
            shrink: true,
          }}
        />
        <br></br><br></br>
        <Button variant="contained" >
          Upload
        </Button>
      </label>
      </div> :
      <div className='container'>
      <h1>DECRYPTION</h1>
        <div className='typewriter'>
          <h3>Upload encpt image along with key..</h3>
        </div>
        <label htmlFor="contained-button-file">
        <Input accept="image/*" id="contained-button-file" multiple type="file" onChange={upload} />
        <Button variant="contained" component="span">
          Choose file
        </Button>
        <br></br><br></br>
        {arr[1]}
        <br></br><br></br>
        <TextField
          id="outlined-number"
          label="Number"
          type="number"
          InputLabelProps={{
            shrink: true,
          }}
        />
        <br></br><br></br>
        <Button variant="contained" >
          Upload
        </Button>
      </label>
      </div> }
    </div>
  )
}

export default Decrypt
