import React, { useState } from 'react';
import { Button, Typography } from '@mui/material';
import { Link } from 'react-router-dom';
import { styled } from '@mui/material/styles';

const Input = styled('input')({
  display: 'none',
});

const Encrypt = () => {
  const [vis , setVis] = useState(1);
  const [arr , setArr] = useState("");
  const [key , setKey] = useState("248");

  const [display2, setDisplay2] = useState(
    <div className='container'>
  <h1>ENCRYPTED</h1>
  <div className='typewriter'>
    <h3>This is your encrypted image..</h3>
</div>
  <br></br><br></br>
  <h4>The file name is </h4>
  {arr[1]}
  <br></br><br></br>
  <h4>The key is </h4>
  {key}
  <br></br><br></br>
  <Button variant="contained" >
    Download
  </Button>
</div>);

const [display1, setDisplay1] = useState(<div className='container'>
<h1>ENCRYPTION</h1>
<div className='typewriter'>
  <h3>Upload your image here...</h3>
</div>
<label htmlFor="contained-button-file">
<Input accept="image/*" id="contained-button-file" multiple type="file" onChange={upload} />
<Button variant="contained" component="span">
  Choose file
</Button>
<br></br><br></br>
{arr[1]}
<br></br><br></br>
<Button variant="contained" onClick={layout} >
  Upload
</Button>
</label>
</div>);

  const upload =(e)=>{
    var str=e.target.value
    var arr=str.split('fakepath');
       setArr(arr)
  }

  const layout = () =>{
    setVis(0);

  }

  return (
    <div>
      <div className='Header'>
      <Link className='link' to="/">
        <Typography variant="h3" >MINI-PROJECT</Typography>
        </Link>
        <div></div>
        <div></div>
      </div>
      {vis && {display1}};
    </div>
  )
}

export default Encrypt
