  }

  const postData =()=>{
    axios({
      method: "post",
      url: "http://127.0.0.1:8000/fileencrypt",
      data: bodyFormData,
      headers: { "Content-Type": "multipart/form-data" },
    })
      .then(function (response) {
        //handle success
        console.log(response);
      })
      .catch(function (response) {
        //handle error
        console.log(response);
      });
