<Button variant="contained" >
  Download
</Button>
</div>

const [layout , setLayout] = useState(display1);

  return (
    <div>
      <div className='Header'>
      <Link className='link' to="/">
        <Typography variant="h3" >MINI-PROJECT</Typography>
        </Link>
        <div></div>
        <div></div>
      </div>
      {layout}
    </div>
  )
}

export default Encrypt
