      data: bodyFormData,
      headers: { "Content-Type": "multipart/form-data" },
    })
      .then(function (response) {
        //handle success
        console.log(response);
        console.log(response.data.name)
        setName(response.data.name)
        console.log(response.data.key)
        setKey(response.data.key)
