import React, { useState } from 'react';
import axios from 'axios';
import { Button, Typography, TextField } from '@mui/material';
import { Link } from 'react-router-dom';
import { styled } from '@mui/material/styles';

const Input = styled('input')({
  display: 'none',
});

const Decrypt = () => {
  const [arr , setArr] = useState("");
  const [vari , setVari] = useState();
  const [key , setKey] = useState();

  const upload =(e)=>{
    var str=e.target.value
    var arr=str.split('\\fakepath\\');
       setArr(arr)
  }

  const postData =()=>{
    axios({
      method: "post",
      url: "http://127.0.0.1:8000/fileencrypt",
      data: bodyFormData,
      headers: { "Content-Type": "multipart/form-data" },
    })
      .then(function (response) {
        //handle success
        console.log(response);
        setName(response.data.name)
        setKey(response.data.key)
      })
      .catch(function (response) {
        //handle error
        console.log(response);
      });
  }

  const getData =()=>{
    axios({
      method: "get",
      url: "http://127.0.0.1:8000/return-encfile",
      responseType:'blob'
    })
      .then((response) => {
        const url = window.URL.createObjectURL(new Blob([response.data],
          {type: response.headers['content-type']
        })
        );
        const link = document.createElement('a')
        link.href = url
        link.setAttribute('download','enc.png')
        document.body.appendChild(link)
        link.click()
        //handle success
        console.log(response);
      })
      .catch(function (response) {
        //handle error
        console.log(response);
      });
  }



  return (
    <div>
      <div className='Header'>
      <Link className='link' to="/">
        <Typography variant="h3" >MINI-PROJECT</Typography>
      </Link>
        <div></div>
      </div>
      {1 && vari ? <div className='container'>
      <h1>DECRYPTED</h1>
        <div className='typewriter'>
          <h3>The decrypted image is..</h3>
        </div>
        <Button variant="contained" >
          Download
        </Button>
      </div> :
      <div className='container'>
      <h1>DECRYPTION</h1>
        <div className='typewriter'>
          <h3>Upload encpt image along with key..</h3>
        </div>
        <label htmlFor="contained-button-file">
        <Input accept="image/*" id="contained-button-file" multiple type="file" onChange={upload} />
        <Button variant="contained" component="span">
          Choose file
        </Button>
        <br></br><br></br>
        {arr[1]}
        <br></br><br></br>
        <TextField
          id="outlined-number"
          label="Number"
          type="number"
          InputLabelProps={{
            shrink: true,
          }}
        />
        <br></br><br></br>
        <Button variant="contained" >
          Upload
        </Button>
      </label>
      </div> }
    </div>
  )
}

export default Decrypt
